mapper.delete:

delete from article_dir 
 where id IN

     #{id}

mapper.select:
select 
 id, author_id, dir_name, create_time, update_time,  
effective 
from article_dir 
where 1=1 

and id = #{id}


and author_id = #{authorId}


and dir_name = #{dirName}


and create_time = #{createTime}


and update_time = #{updateTime}


and effective = #{effective}
